    def setupSql(self):
        # Melakukan setup koneksi SQL
        self.DB_NAME = get_DB_NAME()
        self.db = mysql.connector.connect(**get_config())
        self.cursor = self.db.cursor()

    def hideAman(self):
        self.todo_aman.setVisible(False)

    def showAman(self):
        self.todo_aman.setVisible(True)
    
    def hideBahaya(self):
        self.todo_bahaya.setVisible(False)

    def showBahaya(self):
        self.todo_bahaya.setVisible(True)

    def showAlert(self,err):
        # Menampilkan pesan kesalahan
        msg = QtWidgets.QMessageBox()
        msg.setWindowTitle("Peringatan")
        msg.setIcon(QtWidgets.QMessageBox.Critical)
        msg.setText(err)
        x = msg.exec_()

    def showSucc(self,succ):
        msg = QtWidgets.QMessageBox()
        msg.setWindowTitle("Berhasil")
        msg.setIcon(QtWidgets.QMessageBox.Information)
        msg.setText(succ)
        x = msg.exec_()

    def refreshLatestSuhu(self):
        result = get_latest_keadaan_by_id(self.cursor, self.DB_NAME, self.ID_Pengguna)
        if(result=="Bahaya"):
                self.hideAman()
                self.showBahaya()
        else:
                self.hideBahaya()
                self.showAman()

    def cekSuhu(self):
        new = self.input_suhu.text()
        result = set_riwayat_by_id(self.db, self.cursor, self.DB_NAME, self.ID_Pengguna, new)
        if(result[0]==0):
                self.showAlert(result[1])
                self.input_suhu.setText("")
        else:
                self.showSucc(result[1])
                self.input_suhu.setText("")
        self.refreshLatestSuhu()

